const input = $input.first().json;
const chat_id = input.chat_id;
const lang = input.lang || 'en';
const reservation_id = (input.arg1 || '').trim();

if (!reservation_id) {
  return [{ json: {
    chat_id,
    lang,
    error: true,
    error_key: 'delete_missing_id'
  }}];
}

return [{ json: {
  chat_id,
  lang,
  user_id: input.user_id,
  reservation_id: reservation_id
}}];

const template = $input.first().json.text;
const ctx = $('Set Delete Context').first().json;

// Format date for display (DB stores as YYYY-MM-DD)
let dateDisplay = ctx.date;
if (dateDisplay && dateDisplay.includes('-')) {
  const parts = dateDisplay.split('T')[0].split('-');
  dateDisplay = `${parts[2]}.${parts[1]}.${parts[0]}`;
}

const message = template
  .replace('{date}', dateDisplay)
  .replace('{time_start}', ctx.time_start)
  .replace('{time_end}', ctx.time_end)
  .replace('{court}', ctx.court);

return [{
  json: {
    chat_id: ctx.chat_id,
    message: message
  }
}];

